package com.udemy;import org.apache.kafka.clients.admin.NewTopic;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.kafka.config.TopicBuilder;@Configurationpublic class CreateTopic {    @Bean    public NewTopic topic1(){        return TopicBuilder.name("TOPIC-1").partitions(3).replicas(1).build();    }}